const list_items=document.querySelectorAll("li");list_items.forEach((e=>{e.addEventListener("click",(()=>{e.classList.contains("list-item-non-selected")?(list_items.forEach((e=>{e.classList.remove("list-item-selected"),e.classList.add("list-item-non-selected")})),e.classList.remove("list-item-non-selected"),e.classList.add("list-item-selected")):(e.classList.remove("list-item-selected"),e.classList.add("list-item-non-selected"))}))}));